import React, { useEffect, useState } from "react";

export const App = () => {

    const [padding, setPadding] = useState(0)

    useEffect(()=> {
        setInterval(() => {
            setPadding((oudPadding) => {
                return oudPadding + 1
            })
        }, 2000)
    })

  return (
    <div style={{backgroundColor: 'black', color: 'white', padding}}>
        Teste
    </div>
  )
};
